SELECT ROUND(t1.[最高价(元)] - t1.[最低价(元)],3) AS 日价格振幅
FROM 港股票日行情表 t1
WHERE t1.[股票代码] = '37201' AND t1.[交易日] = '20150716';

SELECT t1.[基金类型]
FROM 基金基本信息 t1
WHERE t1.[基金代码] = '114514';

SELECT COUNT(DISTINCT t1.[基金代码])
FROM 基金规模变动表 t1
WHERE t1.[截止日期] = '2015-03-31 00:00:00' AND t1.[报告期期末基金总份额] < t1.[报告期期初基金总份额];
